import React from 'react';
import Loading from './Loading';

class Randomuser extends React.Component{

  constructor(){
    super()

    this.state = {
      person: [],
      loading: true,
    }
  }

  componentDidMount() {
    const url = 'https://api.randomuser.me/ ';
    fetch(url)
    .then((response) => response.json())
    .then((data) => {
      this.setState({
        person: data.results,
        loading: false,
      })
    })
  }

