import React, { useState } from 'react';

function Form() {
  const [name, setName] = useState('');

  return (
    <form>
      <fieldset>
        <label htmlFor="name">
          Name: 
        <input
        id="name"
        type="text"
        value={name}
        onChange={ ({target}) => setName(target.name)}>
        </input>
        </label>
      </fieldset>
    </form>
  );
}

export default Form;
